INSERT INTO tb_books (title, book_year, publisher, genre, edition,  short_summary, long_summary, fk_author) values('O homem mais rico da Babiônia', '1926', 'Harper Collins', 'Auto-ajuda e Desenvolvimento Pessoal', 1,'Foi por volta de 1926 que o autor George S. Clason se lembrou de escrever alguns panfletos informativos sobre como alcançar o sucesso financeiro...','Foi por volta de 1926 que o autor George S. Clason se lembrou de escrever alguns panfletos informativos sobre como alcançar o sucesso financeiro, inspirando-se em parábolas ambientadas na antiga Babilónia, império indubitavelmente poderoso. Assim, Clason conta a história de um homem que quis tornar-se rico e o conseguiu através de cinco leis de ouro, que se tornaram um conjunto de princípios pedagógicos com validade intemporal.',3)
INSERT INTO tb_books (title, book_year, publisher, genre, edition,  short_summary, long_summary, fk_author) values('Pai rico, Pai pobre', '1997', 'Campus- Grupo Elsevier', 'Finanças pessoais', 1, 'O livro Pai rico, pai pobre narra a história de Robert Kiyosaki e de seu amigo Mike. Ao longo de sua trajetória...','O livro Pai rico, pai pobre narra a história de Robert Kiyosaki e de seu amigo Mike. Ao longo de sua trajetória, Robert recebe orientações financeiras de seu próprio pai – o pai pobre – e do pai de Mike – o pai rico. suas próprias carreiras, eles carregam visões muito distintas quando se fala em lidar com o dinheiro.',1)
INSERT INTO tb_books (title, book_year, publisher, genre, edition,  short_summary, long_summary, fk_author) values('As 16 leis do sucesso', '2017','Faro Editorial','Auto-ajuda e Desenvolvimento Pessoal', 2,'AS 16 LEIS DO SUCESSO é um curso prático que reúne as 16 poderosas lições que são as bases de toda filosofia de Napoleon Hill...','AS 16 LEIS DO SUCESSO é um curso prático que reúne as 16 poderosas lições que são as bases de toda filosofia de Napoleon Hill, o maior gênio na área da realização pessoal e psicologia aplicada de todos os tempos.',2)